import React,{useState} from 'react'
export default function Textform(props) {

    const toUpperCase =(event)=>{
        event.preventDefault();
        console.log(""+text)
         let nEwText = text.toUpperCase();
        setText(nEwText)
       }
    const tolowerCase =(event)=>{
        event.preventDefault();
         let nEwText = text.toLowerCase();
        setText(nEwText)
       }
    const clearText =(event)=>{
        event.preventDefault();
         let nEwText = "";
        setText(nEwText)
    }
    const EmailExtractor =(event)=>{
        event.preventDefault();
        let nEwtExt = text;
        let EmailFinder=setText(nEwtExt)
        if(EmailFinder ==="@gmail.com"){
          console.log("there is a gmail")
          console.log(EmailFinder)
          console.log(text)
        }
        else{
          console.log("no email")
          console.log(EmailFinder)
          console.log(text)
        }
        
    }
    const handelOnChange = (event)=>{
    console.log("on change")
    setText(event.target.value)

    }
    const [text,setText]=useState("");
  return (
    <>
    <h1>{props.heading}</h1>
    <form>
  <div className="form-group">
    <textarea className="form-control" id="exampleFormControlTextarea1" rows="8" value={text} onChange={handelOnChange}></textarea>
  </div>
  <button className='btn btn-primary my-3 mx-1'onClick={toUpperCase}>Convert into UpperCase </button>
  <button className='btn btn-primary my-3 mx-1'onClick={tolowerCase}>Convert into lowerCase </button>
  <button className='btn btn-primary my-3 mx-1'onClick={clearText}>Clear Text </button>
  <button className='btn btn-primary my-3 mx-1'onClick={EmailExtractor}>EmailExtractor </button>
</form>
<div className='container'>
  <h1> Your Para Summary</h1>
  <p>word {text.split(" ").length} and {text.length}characters</p>
  <p>{0.008*text.split(" ").length} Minutes to read</p>
  <h2>Preview</h2>
  <p>{text}</p>
</div>
</>
  )
}
