import React,{useState} from 'react'
export default function Textform(props) {

    const toUpperCase =(event)=>{
        event.preventDefault();
        console.log("convert into Uppercase"+text)
         let nEwText = text.toUpperCase();
        setText(nEwText)
       }
    const tolowerCase =(event)=>{
        event.preventDefault();
        console.log("convert into lowercase"+text)
         let nEwText = text.toLowerCase();
        setText(nEwText)
    }
    const handelOnChange = (event)=>{
    console.log("on change")
    setText(event.target.value)

    }
    const [text,setText]=useState("enter Text Here");
  return (
    <>
    <h1>{props.heading}</h1>
    <form>
  <div className="form-group">
    <textarea className="form-control" id="exampleFormControlTextarea1" rows="8" value={text} onChange={handelOnChange}></textarea>
  </div>
  <button className='btn btn-primary my-3 mx-3'onClick={toUpperCase}>Convert into UpperCase </button>
  <button className='btn btn-primary my-3 'onClick={tolowerCase}>Convert into lowerCase </button>
</form>
<div className='container'>
  <h1> Your Para Summary</h1>
  <p>word {text.split(" ").length} and {text.length}characters</p>
  <p>{0.008*text.split(" ").length} Minutes to read</p>
</div>
</>
  )
}
