import React, { useState } from 'react';

export default function Textform(props) {
  const [text, setText] = useState("enter Text Here");

  const toLowerCase = (event) => {
    event.preventDefault(); // Prevent page reload
    setText(text.toLowerCase()); // Convert the text to lowercase
    console.log("convert into lowercase");
  };

  const handelOnChange = (event) => {
    setText(event.target.value); // Update the text state with user input
  };

  return (
    <>
      <h1>{props.heading}</h1>
      <form>
        <div className="form-group">
          <textarea
            className="form-control"
            id="exampleFormControlTextarea1"
            rows="8"
            value={text}
            onChange={handelOnChange}
          ></textarea>
        </div>
        <button className='btn btn-primary my-3' onClick={toLowerCase}>
          Convert into lower case
        </button>
      </form>
    </>
  );
}
